import React, {useState} from 'react';
import arrowLogo from './Arrow_Back.png';
import arrowLogoHover from './Arrow_Back_hover.png';
import './Arrow.css';

type Props = {
  className?: string;
  direction: 'left' | 'right';
  onClick?: () => void;
};

function Arrow(props: Props): JSX.Element {
  const {direction} = props;
  const [hover, setHover] = useState(false);
  return (
    <img
      className={`arrow-back arrow-${direction} ${props.className ?? ''}`}
      src={hover ? arrowLogoHover : arrowLogo}
      onClick={props.onClick}
      onMouseOver={() => setHover(true)}
      onMouseLeave={() => setHover(false)}
    />
  );
}

export default Arrow;
