import React, {useCallback, useState} from 'react';
import Modal from 'react-modal';
import Carousel from 'react-bootstrap/Carousel';
import './ModalCarousel.css';
import videojs, {VideoJsPlayer} from 'video.js';
import closeBtn from '../../assets/img/button_close.png';
import closeBtn2x from '../../assets/img/button_close@2x.png';
import backgroundMobile from '../../assets/img/Background_Slideshow_Mobile.png';
import backgroundMobile2x from '../../assets/img/Background_Slideshow_Mobile@2x.png';

const options = {
  html5: true,
  controls: false,
  fluid: true,
  preload: 'auto',
};

const useRefWithVideoJs = (onPlayEnd: () => void) => {
  const [ref, setRef] = useState<HTMLDivElement | null>(null);
  const [videoJs, setVideoJs] = useState<VideoJsPlayer>();

  const setContainerRef = useCallback((node) => {
    if (ref !== null) {
      videoJs?.dispose();
      setVideoJs(undefined);
    }
    if (node !== null) {
      const vjsPlayer = videojs(node, options);
      vjsPlayer.one('ended', () => {
        console.log('ended');
        onPlayEnd();
      });
      vjsPlayer.muted(true);
      vjsPlayer.loop(false);
      vjsPlayer.autoplay('muted');
      vjsPlayer.autoplay(true);
      setVideoJs(vjsPlayer);
    }
    setRef(node);
  }, []);
  return [setContainerRef];
};

Modal.setAppElement('#root');

const styles: Modal.Styles = {
  overlay: {
    zIndex: 10,
    background: 'none',
  },
  content: {
    border: 'none',
    padding: 0,
    backgroundColor: 'black',
    top: 30,
    bottom: 30,
    left: 20,
    right: 20,
  },
};

type Props = {
  isOpen: boolean;
  video1: string;
  video1Webm: string;
  video2: string;
  video2Webm: string;
  citation: string;
  citationSmall: string;
  image: string;
  image2x: string;
  imageMobileBackground: string;
  imageMobileBackground2x: string;
  imageTitle: string;
  description: string;
  onClose?: () => void;
};

const ModalCarousel = (props: Props): JSX.Element => {
  const {
    isOpen,
    onClose,
    video1,
    video1Webm,
    video2,
    video2Webm,
    citation,
    citationSmall,
    image,
    image2x,
    imageTitle,
    description,
    imageMobileBackground,
    imageMobileBackground2x,
  } = props;
  const [slideIndex, setSlideIndex] = useState(0);
  const [video1Ref] = useRefWithVideoJs(() => console.log('video1 ended'));
  const [video2Ref] = useRefWithVideoJs(() => console.log('video2 ended'));
  const backgroundImage = slideIndex === 2 ? `url(${imageMobileBackground})` : `url(${backgroundMobile})`;
  const background2x = slideIndex === 2 ? `url(${imageMobileBackground2x})` : `url(${backgroundMobile2x})`;
  return (
    <Modal isOpen={isOpen} style={styles}>
      <div
        className="modal-carousel"
        style={{
          ['--mobile-background-image' as any]: backgroundImage,
          ['--mobile-background-image-2x' as any]: background2x,
        }}>
        <Carousel interval={null} activeIndex={slideIndex} onSelect={(index) => setSlideIndex(index)}>
          <Carousel.Item>
            <video
              ref={video1Ref}
              className="video-js vjs-default-skin vjs-big-play-centered custom-videojs"
              controls={false}>
              <source src={video1} />
              <source src={video1Webm} />
            </video>
          </Carousel.Item>
          <Carousel.Item>
            <div className="section-2">
              <div className="section-2-content">
                <h2 className="citation">{citation}</h2>
                <h2 className="serge">Serge Lutens</h2>
                <hr />
                <blockquote>{citationSmall}</blockquote>
              </div>
            </div>
          </Carousel.Item>
          <Carousel.Item>
            <div className="section-3">
              <div className="section-3-content">
                <img src={image} srcSet={image2x} />
                <div>
                  <h3>{imageTitle}</h3>
                  <p>{description}</p>
                </div>
              </div>
            </div>
          </Carousel.Item>
          <Carousel.Item>
            <video
              ref={video2Ref}
              className="video-js vjs-default-skin vjs-big-play-centered custom-videojs"
              controls={false}>
              <source src={video2} />
              <source src={video2Webm} />
            </video>
          </Carousel.Item>
        </Carousel>
        <div className="close-btn">
          <img src={closeBtn} srcSet={closeBtn2x} onClick={onClose} />
        </div>
      </div>
    </Modal>
  );
};

export default ModalCarousel;
