import React from 'react';
import {withTranslation, WithTranslation} from 'react-i18next';
import {connect} from 'react-redux';
import {RouteComponentProps, withRouter} from 'react-router-dom';
import {Dispatch} from 'redux';
import Arrow from 'src/components/arrow/Arrow';
import ModalCarousel from 'src/components/modalcarousel/ModalCarousel';
import {ReduxAppState} from 'src/store';
import {hasSeenStep1} from 'src/store/root/actions';
import {RootActionTypes} from 'src/store/root/types';
import VideoModal from '../../components/videomodal/VideoModal';
import flower from './Flower_C_Lab_1.png';
import flower2x from './Flower_C_Lab_1@2x.png';

import video1 from './VIDEO 1_desktop.mp4';
import video1webm from './VIDEO 1_desktop.webm';
import videoa from './VIDEO A_desktop.mp4';
import videoawebm from './VIDEO A_desktop.webm';

import './PriseConscience.css';

type StateProps = {
  seenStep1: boolean;
};

type DispatchProps = {
  hasSeenStep1: () => RootActionTypes;
};

type Prop = WithTranslation & RouteComponentProps & StateProps & DispatchProps;
type State = {
  showModalCarousel: boolean;
};

class PriseConscience extends React.Component<Prop, State> {
  constructor(props: Prop) {
    super(props);
    this.state = {
      showModalCarousel: false,
    };
  }
  render() {
    const {showModalCarousel} = this.state;
    const {history, seenStep1, hasSeenStep1, t, i18n} = this.props;
    return (
      <div
        className="prise-conscience"
        style={{['--background-image' as any]: `var(--background-image-${i18n.language.toLocaleLowerCase()})`}}>
        <VideoModal
          isOpen={seenStep1 === false}
          video={video1}
          videoWebm={video1webm}
          onVideoEnded={() => hasSeenStep1()}
        />
        <ModalCarousel
          isOpen={showModalCarousel}
          video1={videoa}
          video1Webm={videoawebm}
          video2={videoa}
          video2Webm={videoawebm}
          citation={t('priseconscience.decettefillefroide')}
          citationSmall={t('priseconscience.leprisedeconscience')}
          image={flower}
          image2x={flower2x}
          imageMobileBackground={''}
          imageMobileBackground2x={''}
          imageTitle={t('priseconscience.ylang')}
          description={t('priseconscience.unebouteillejeteealamer')}
          onClose={() => this.setState({showModalCarousel: false})}
        />
        <div className="section-arrow-left">
          <Arrow direction="left" onClick={() => history.push({pathname: 'main'})} />
        </div>
        <div className="clickable-zone" onClick={() => this.setState({showModalCarousel: true})} />
        <div className="section-arrow-right">
          <Arrow direction="right" onClick={() => history.push({pathname: 'delivrance'})} />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state: ReduxAppState): StateProps => {
  return {
    seenStep1: state.seenStep1,
  };
};

const mapDispatchToProps = (dispatch: Dispatch): DispatchProps => {
  return {
    hasSeenStep1: () => dispatch(hasSeenStep1()),
  };
};

export default connect<StateProps, DispatchProps, unknown, ReduxAppState>(
  mapStateToProps,
  mapDispatchToProps,
)(withTranslation()(withRouter(PriseConscience)));
