import React from 'react';
import { withTranslation, WithTranslation } from 'react-i18next';
import { RouteComponentProps, withRouter } from 'react-router';
import './Login.css';
import lockImg from './LOCK.png';
import lockImg2x from './LOCK@2x.png';
import DigitInput from '../../components/digitinput/DigitInput';
import LogoSerge from 'src/components/logoserge/LogoSerge';
import { ReduxAppState } from '../../store';
import { Dispatch } from 'redux';
import { connect } from 'react-redux';
import { isAuthentifcated } from 'src/store/root/actions';
import { RootActionTypes } from 'src/store/root/types';

const validcode = {
    '29547': 'EN',
    '27683': 'FR',
    '37294': 'EN',
    '36842': 'EN',
    '46273': 'ES',
    '43582': 'EN',
    '59376': 'EN',
    '52843': 'EN',
    '64823': 'EN',
    '63598': 'FR',
    '78569': 'EN',
    '72495': 'EN',
    '83296': 'EN',
    '86572': 'EN',
    '95426': 'EN',
    '92857': 'EN',
    '97364': 'EN',
    '25894': 'FR',
};

type StateProps = {
    hasValidateAuthentification: boolean;
};

type DispatchProps = {
    isAuthentifcated: () => RootActionTypes;
};

type Prop = WithTranslation & RouteComponentProps & StateProps & DispatchProps;
type State = {
    value: string;
};

class Login extends React.Component<Prop, State> {
    constructor(prop: Prop) {
        super(prop);
        this.state = {
            value: '',
        };
        if (this.props.hasValidateAuthentification) {
            this.props.history.push({ pathname: 'main' });
        }
    }

    _onChange = (value: string) => {
        this.setState({ value });
        if (Object.keys(validcode).includes(value)) {
            this.setState({ value });
            // eslint-disable-next-line @typescript-eslint/ban-ts-comment
            // @ts-ignore
            this.props.i18n.changeLanguage(validcode[value]);
            this.props.isAuthentifcated();
            this.props.history.push({ pathname: 'main' });
        }
    };

    render(): JSX.Element {
        const { t } = this.props;
        const { value } = this.state;
        return (
            <div className="login">
                <LogoSerge className="logo" />
                <div className="center">
                    <div className="img-hover-zoom img-hover-zoom--point-zoom">
                        <img className="lock" src={lockImg} srcSet={lockImg2x} />
                    </div>
                    <div className="unlockgroup">
                        <p className="unlock">{t('login.unlock')}</p>
                        <p className="thesecret">{t('login.thesecret')}</p>
                    </div>
                    <div className="form">
                        <p className="enter">{t('login.enter')} 29547</p>
                        <DigitInput value={value} onChange={this._onChange} />
                    </div>
                    <p className="hashtag">{t('login.hashtag')}</p>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state: ReduxAppState): StateProps => {
    return {
        hasValidateAuthentification: state.hasValidateAuthentification,
    };
};

const mapDispatchToProps = (dispatch: Dispatch): DispatchProps => {
    return {
        isAuthentifcated: () => dispatch(isAuthentifcated()),
    };
};

export default connect<StateProps, DispatchProps, unknown, ReduxAppState>(
    mapStateToProps,
    mapDispatchToProps,
)(withTranslation()(withRouter(Login)));
