import {isAuthentifcated, hasSeenStep1, hasSeenStep2, hasSeenStep3, hasCompletedXp, hasVoted} from './actions';
import {
  RootActionTypes,
  RootState,
  ROOT_IS_AUTHENTICATED,
  ROOT_HAS_SEEN_STEP1,
  ROOT_HAS_SEEN_STEP2,
  ROOT_HAS_SEEN_STEP3,
  ROOT_HAS_VOTED,
  ROOT_HAS_COMPLETED_XP,
} from './types';

export type Actions =
  | ReturnType<typeof isAuthentifcated>
  | ReturnType<typeof hasSeenStep1>
  | ReturnType<typeof hasSeenStep2>
  | ReturnType<typeof hasSeenStep3>
  | ReturnType<typeof hasVoted>
  | ReturnType<typeof hasCompletedXp>;

export const initialState: RootState = {
  hasValidateAuthentification: false,
  seenStep1: false,
  seenStep2: false,
  seenStep3: false,
  hasVoted: false,
  completedXp: false,
};

export function rootReducer(state: RootState = initialState, action: RootActionTypes): RootState {
  console.log({action});
  switch (action.type) {
    case ROOT_IS_AUTHENTICATED:
      return {...state, hasValidateAuthentification: true};
    case ROOT_HAS_SEEN_STEP1:
      return {...state, seenStep1: true};
    case ROOT_HAS_SEEN_STEP2:
      return {...state, seenStep2: true};
    case ROOT_HAS_SEEN_STEP3:
      return {...state, seenStep3: true};
    case ROOT_HAS_VOTED:
      return {...state, hasVoted: true};
    case ROOT_HAS_COMPLETED_XP:
      return {...state, completedXp: true};
    default:
      return state;
  }
}
