import React from 'react';
import {withTranslation, WithTranslation} from 'react-i18next';
import {connect} from 'react-redux';
import {RouteComponentProps, withRouter} from 'react-router-dom';
import {Dispatch} from 'redux';
import Arrow from 'src/components/arrow/Arrow';
import {ReduxAppState} from 'src/store';
import {hasSeenStep2} from 'src/store/root/actions';
import {RootActionTypes} from 'src/store/root/types';
import VideoModal from '../../components/videomodal/VideoModal';
import ModalCarousel from 'src/components/modalcarousel/ModalCarousel';
import {isMobile} from 'mobile-device-detect';

import flower from './Flower_D_Lab_2.png';
import flower2x from './Flower_D_Lab_2@2x.png';
import imageMobileBackground from './Background_1-2_Slideshow_Mobile.png';
import imageMobileBackground2x from './Background_1-2_Slideshow_Mobile@2x.png';

import video2mp4 from './VIDEO 2_desktop.mp4';
import video2webm from './VIDEO 2_desktop.webm';
import videobmp4 from './VIDEO B_desktop.mp4';
import videobwebm from './VIDEO B_desktop.webm';

import './Delivrance.css';

type StateProps = {
  seenStep2: boolean;
};

type DispatchProps = {
  hasSeenStep2: () => RootActionTypes;
};

type Prop = WithTranslation & RouteComponentProps & StateProps & DispatchProps;
type State = {
  showModalCarousel: boolean;
};
class Delivrance extends React.Component<Prop, State> {
  constructor(props: Prop) {
    super(props);
    this.state = {
      showModalCarousel: false,
    };
  }
  render() {
    const {showModalCarousel} = this.state;
    const {history, seenStep2, hasSeenStep2, t, i18n} = this.props;
    return (
      <div
        className="delivrance"
        style={{['--background-image' as any]: `var(--background-image-${i18n.language.toLocaleLowerCase()})`}}>
        <VideoModal
          isOpen={seenStep2 === false}
          video={video2mp4}
          videoWebm={video2webm}
          onVideoEnded={() => hasSeenStep2()}
        />
        <ModalCarousel
          isOpen={showModalCarousel}
          video1={videobmp4}
          video1Webm={videobwebm}
          video2={videobmp4}
          video2Webm={videobwebm}
          citation={t('delivrance.cequavulablancheur')}
          citationSmall={t('delivrance.ladelivrance')}
          image={flower}
          image2x={flower2x}
          imageMobileBackground={imageMobileBackground}
          imageMobileBackground2x={imageMobileBackground2x}
          imageTitle={t('delivrance.fleurfrangipanes')}
          description={t('delivrance.ledouceetdelicatefleur')}
          onClose={() => this.setState({showModalCarousel: false})}
        />
        <div className="section-arrow-left">
          <Arrow direction="left" onClick={() => history.push({pathname: 'priseconscience'})} />
        </div>
        <div className="clickable-zone" onClick={() => this.setState({showModalCarousel: true})} />
        <div className="section-arrow-right">
          <Arrow direction="right" onClick={() => history.push({pathname: 'liberte'})} />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state: ReduxAppState): StateProps => {
  return {
    seenStep2: state.seenStep2,
  };
};

const mapDispatchToProps = (dispatch: Dispatch): DispatchProps => {
  return {
    hasSeenStep2: () => dispatch(hasSeenStep2()),
  };
};

export default connect<StateProps, DispatchProps, unknown, ReduxAppState>(
  mapStateToProps,
  mapDispatchToProps,
)(withTranslation()(withRouter(Delivrance)));
