import React from 'react';
import {withTranslation, WithTranslation} from 'react-i18next';
import {connect} from 'react-redux';
import {RouteComponentProps, withRouter} from 'react-router-dom';
import {Dispatch} from 'redux';
import Arrow from 'src/components/arrow/Arrow';
import {ReduxAppState} from 'src/store';
import {hasSeenStep3} from 'src/store/root/actions';
import {RootActionTypes} from 'src/store/root/types';
import VideoModal from '../../components/videomodal/VideoModal';
import ModalCarousel from 'src/components/modalcarousel/ModalCarousel';

import video3 from './VIDEO 3_desktop.mp4';
import video3webm from './VIDEO 3_desktop.webm';
import video4 from './VIDEO 4_desktop.mp4';
import video4webm from './VIDEO 4_desktop.webm';

import flower from './Amandes_E_Lab_3.png';
import flower2x from './Amandes_E_Lab_3@2x.png';
import imageMobileBackground from './Background_3-2_Slideshow_Mobile.png';
import imageMobileBackground2x from './Background_3-2_Slideshow_Mobile@2x.png';

import './Liberte.css';

type StateProps = {
  seenStep3: boolean;
};

type DispatchProps = {
  hasSeenStep3: () => RootActionTypes;
};

type Prop = WithTranslation & RouteComponentProps & StateProps & DispatchProps;
type State = {
  lastModalOpened: boolean;
  showModalCarousel: boolean;
};

class Liberte extends React.Component<Prop, State> {
  constructor(props: Prop) {
    super(props);
    this.state = {
      lastModalOpened: false,
      showModalCarousel: false,
    };
  }

  _onFinish = () => {
    this.setState({lastModalOpened: false});
    this.props.history.push({pathname: 'fiole'});
  };

  render() {
    const {history, seenStep3, hasSeenStep3, t, i18n} = this.props;
    const {lastModalOpened, showModalCarousel} = this.state;
    return (
      <div
        className="liberte"
        style={{['--background-image' as any]: `var(--background-image-${i18n.language.toLocaleLowerCase()})`}}>
        <VideoModal
          isOpen={seenStep3 === false}
          video={video3}
          videoWebm={video3webm}
          onVideoEnded={() => hasSeenStep3()}
        />
        <ModalCarousel
          isOpen={showModalCarousel}
          video1={video4}
          video1Webm={video4webm}
          video2={video4}
          video2Webm={video4webm}
          citation={t('liberte.subtilementamande')}
          citationSmall={t('liberte.lalibertecestdevoir')}
          image={flower}
          image2x={flower2x}
          imageMobileBackground={imageMobileBackground}
          imageMobileBackground2x={imageMobileBackground2x}
          imageTitle={t('liberte.amandes')}
          description={t('liberte.lesquelquesnotesdamande')}
          onClose={() => this.setState({showModalCarousel: false})}
        />
        <VideoModal isOpen={lastModalOpened} video={video4} videoWebm={video4webm} onVideoEnded={this._onFinish} />
        <div className="section-arrow-left">
          <Arrow direction="left" onClick={() => history.push({pathname: 'delivrance'})} />
        </div>
        <div className="clickable-zone" onClick={() => this.setState({showModalCarousel: true})} />
        <div className="section-arrow-right">
          <Arrow direction="right" onClick={() => this.setState({lastModalOpened: true})} />
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state: ReduxAppState): StateProps => {
  return {
    seenStep3: state.seenStep3,
  };
};

const mapDispatchToProps = (dispatch: Dispatch): DispatchProps => {
  return {
    hasSeenStep3: () => dispatch(hasSeenStep3()),
  };
};

export default connect<StateProps, DispatchProps, unknown, ReduxAppState>(
  mapStateToProps,
  mapDispatchToProps,
)(withTranslation()(withRouter(Liberte)));
